<?php// 文章模型控制器//require 'vendor/autoload.php';//use EasyWeChat\Foundation\Application;class IndexController extends BaseController{    //>>首页    public function indexAction(){        $page = !empty($_GET["page"])?$_GET["page"]:1;        //>>查询首页展示数据        $model = new ModelNew("cases");        $num = 7*$page;        $indexDatas = $model->findBySql("select * from sl_cases WHERE shouye='是' limit $num");        //>获取首页展示总数        $total = count($indexDatas);        $totalPage = ceil($total/7);        include CUR_VIEW_PATH . "Sindex" . DS . "index_index.html";    }    //>>关于我们    public function aboutAction(){        //>>获取页面图片        $model = new ModelNew("about_picture");        $data = $model->findBySql("select picture from sl_about_picture ORDER by id ASC limit 1");        $picture = !empty($data[0]["picture"])?$data[0]["picture"]:"";        //>>获取文章资料        $model = new ModelNew("article");        $datalist = $model->findBySql("select * from sl_article");        include CUR_VIEW_PATH . "Sindex" . DS . "index_about.html";    }    //>>案例    public function casesAction(){        $type = !empty($_GET["type"])?$_GET["type"]:'包装设计';        $model = new ModelNew("cases");        $datas = $model->findBySql("select * from sl_cases WHERE fenlei like '%$type%' limit 8");        include CUR_VIEW_PATH . "Sindex" . DS . "index_cases.html";    }    //>>获取案例详情    public function detailAction(){        $id = $_POST["casesId"];        $model = new ModelNew("cases");        $data = $model->findBySql("select * from sl_cases WHERE id=$id");        echo json_encode($data[0]);    }    //>>保存联系人    public function lianxirenAction(){        $data["lianxiren"] = $_POST["lianxiren"];        $data["qiyemingcheng"] = $_POST["qiyemingcheng"];        $data["lianxidianhua"] = $_POST["lianxidianhua"];        $data["xiangmuxuqiu"] = $_POST["xiangmuxuqiu"];        $model = new ModelNew("qiye");        if ($model->insert($data)){            echo json_encode(["code"=>200,"msg"=>"提交成功"]);        }else{            echo json_encode(["code"=>501,"msg"=>"提交失败"]);        }    }}